import React, { Component } from 'react';
import './App.css';
import Pokecards from '../components/Pokecards/Pokecards';
import Footer from '../components/Footer/Footer'




class App extends Component {

  state = {
    page: 1,
    start: 0,
    end: 49,

  }



  clickHandler = (num) => {

    this.setState({ page: num })
    if (this.state.page === 1) {
      this.setState({
        start: 0,
        end: 39
      })
    }
    else if (this.state.page === 2) {
      this.setState({
        start: 50,
        end: 99
      })
    }
  }





    render() {
      console.log('app.js rendering')







      return (
        <div className="App">
          <h1 className='header'>Welcome to the National Pokedex</h1>
          <Pokecards start={this.state.start}
            end={this.state.end} />
<Footer/>



        </div>
      );
    }
  }

  export default App
