import React from 'react';
import styles from './Pokecard.module.css';




const pokecard = (props) => {

    let cardStyles= ''
    const clickHandler = () => {
        cardStyles = styles.click
    }
    console.log(cardStyles)

    let cardbg = '';

    //Fire Classes
    if (props.abilities == 'Fire' ||
        props.abilities == 'Fire Flying' ||
        props.abilities == 'Fire Rock' ||
        props.abilities == 'Fire Fighting' ||
        props.abilities == 'Fire Ground' ||
        props.abilities == 'Fire Steel' ||
        props.abilities == 'Fire Psychic' ||
        props.abilities == 'Psychic Fire' ||
        props.abilities == 'Fire Normal' ||
        props.abilities == 'Bug Fire') {
        cardbg = styles.fire
    }
    else if (props.abilities == 'Dark Fire' ||
        props.abilities == 'Ghost Fire' ||
        props.abilities == 'Fire Ghost' ||
        props.abilities == 'Fire Dark') {
        cardbg = styles.darkFire
    }
    else if (props.abilities == 'Fire Dragon' ||
        props.abilities == 'Dragon Fire') {
        cardbg = styles.fireDragon
    }
    else if (props.abilities == 'Poison Fire') {
        cardbg = styles.poisonFire
    }
    else if (props.abilities == 'Fire Water') {
        cardbg = styles.fireWater
    }
    //Grass Classes
    else if (props.abilities == 'Grass' ||
        props.abilities == 'Bug Grass' ||
        props.abilities == 'Grass Flying') {
        cardbg = styles.grass
    }
    else if (props.abilities == 'Grass Poison') {
        cardbg = styles.grassPoison
    }
    else if (props.abilities == 'Grass Psychic' ||
        props.abilities == 'Psychic Grass') {
        cardbg = styles.psyGrass
    }
    else if (props.abilities == 'Poison') {
        cardbg = styles.poison
    }
    else if (props.abilities == 'Water') {
        cardbg = styles.water
    }
    else if (props.abilities == 'Bug') {
        cardbg = styles.bug
    }

    return (


        <div className={styles.card + ' ' + cardStyles} onClick={this.clickHandler}>

            <div className={styles.side + ' ' + styles.front}>
                <div className={styles.imgBox + ' ' + cardbg}>
                    <img src={props.imgSrc} alt="notfound" />
                    <p className={styles.id}>{props.id}</p>
                </div>
                <div className={styles.details}>
                    <h3 className={styles.name}>{props.name}</h3>
                    <p className={styles.ability}>{props.abilities}</p>
                </div>
            </div>
            <div className={styles.side + ' ' + styles.back + ' ' + cardbg}>
                <img className={styles.backImg} src={props.imgSrc} alt="notfound" />
            </div>

        </div>


    );
}

export default pokecard
