import React from 'react';
import styles from './Pokecard.module.css';




const pokecard = (props) => {
    let cardbg = '';
    if (props.abilities == 'Fire') {
        cardbg = styles.fire
    }
    else if (props.abilities == 'Grass') {
        cardbg = styles.grass
    }

    // console.log(cardbg)
    return (
        <div className={styles.card}>
            <div className={styles.imgBox + ' ' + cardbg}>
                <img src={props.Imgsrc} alt="notfound" />
                <p className={styles.id}>{props.id}</p>
            </div>
            <div className={styles.details}><h3 className={styles.name}>{props.name}</h3>

            <p className={styles.ability}>{props.abilities}</p></div>


        </div>
    );
}

export default pokecard
