import React, { Component } from 'react';
import './App.css';
import Pokecards from '../components/Pokecards/Pokecards';




class App extends Component {

  state = {
    page: 1,
    start: 0,
    end: 39
  }

  changeHandler = () => {

    if (this.state.page === 1) {
      this.setState({
        start: 0,
        end: 39
      })
    }
    else if (this.state.page === 2) {
      this.setState({
        start: 39,
        end: 79
      })
    }
    // console.log(this.state.start,this.state.end)


  }
  clickHandler = (num) => {
    this.setState({ page: num })
    this.changeHandler()
  }


  render() {
console.log('app.js rendering')





    return (
      <div className="App">
        <h1>Welcome to the National Pokedex</h1>
        <Pokecards start={this.state.start}
          end={this.state.end} />
        <footer style={{
          display: 'block',
          position: 'absolute',
          bottom: '0',
          background: 'black',
          width: '100%'

        }}>
          <li style={{ listStyleType: 'none', background: 'white', color: 'black', width: '3rem', margin: 'auto', cursor: 'pointer' }} onClick={() => this.clickHandler(1)}>1</li>
          <li style={{ listStyleType: 'none', background: 'white', color: 'black', width: '3rem', margin: 'auto', cursor: 'pointer' }} onClick={() => this.clickHandler(2)}>2</li>
          <li style={{ listStyleType: 'none', background: 'white', color: 'black', width: '3rem', margin: 'auto', cursor: 'pointer' }} onClick={() => this.clickHandler(3)}>3</li>

        </footer>


      </div>
    );
  }
}

export default App
