import React, {Component} from 'react';
import './App.css';
import Pokecards from '../components/Pokecards/Pokecards';




class App extends Component {  

  state = {
    page : ''
  }

  render(){
  const clickHandler = (num) => {
    this.setState({page:num})
  }


  return (
    <div className="App">
      <h1>Welcome to the National Pokedex</h1>
      <Pokecards page={page}/>
      <footer style={{display:'block',
      position: 'absolute',
      bottom:'0',
      background:'black',
      width:'100%'

      }}>
        <li style = {{listStyleType:'none',background:'white',color:'black',width:'3rem',margin:'auto',cursor:'pointer'}} onClick={()=>clickHandler(1)}>1</li>
        <li style = {{listStyleType:'none',background:'white',color:'black',width:'3rem',margin:'auto',cursor:'pointer'}} onClick={()=>clickHandler(2)}>2</li>
        <li style = {{listStyleType:'none',background:'white',color:'black',width:'3rem',margin:'auto',cursor:'pointer'}} onClick={()=>clickHandler(3)}>3</li>

      </footer>


    </div>
  );
    }
}

export default App
