import React, { useState} from 'react';
import './App.css';
import Pokecards from '../components/Pokecards/Pokecards';
import Footer from '../components/Footer/Footer'





const App = () => {

const [currentPage, setCurrentPage] = useState(1);
const [ postsPerPage] = useState(50);
const [ totalPosts, setTotalPosts]= useState(809);

const indexOfLastPost = currentPage * postsPerPage;
const indexOfFirstPost = indexOfLastPost - postsPerPage;

const clickHandler = (num) => setCurrentPage(num);

const settingPosts = (posts) => {setTotalPosts(posts)
setCurrentPage(1)};





  return (
    <div className="App">
      <h1 className='header'>Welcome to the National Pokedex</h1>
      <Pokecards  start={indexOfFirstPost} end={indexOfLastPost} setNoOfPosts={settingPosts} />

      <Footer postsPerPage={postsPerPage} totalPosts={totalPosts} clickHandler={clickHandler}/>



    </div>
  );

}

export default App;
