import React, { Component } from 'react';
import Pokecard from './Pokecard/Pokecard';
import './Pokecards.css';
import Sidebar from '../sidebar/Sidebar'

export default class Pokecards extends Component {

  constructor(props) {
    super(props);
    this.setState((state) =>
      state.sidebar = 'sidebar'
    )
  }

  state = {
    data: [],
    showgens: false,
    gentoshow: [],
    searchItem: '',
    showtype: false,
    searchtype: '',
    sideStatus: false,
    sidebar: 'sidebar'

  }

  componentWillMount() {
    const csvFilePath = require('../../assets/poke.csv');
    const Papa = require("papaparse/papaparse.min.js");
    Papa.parse(csvFilePath, {
      header: true,
      download: true,
      skipEmptyLines: true,
      complete: (result) => this.setState({ data: result.data })
    })
  }

  componentDidUpdate(prevProps, prevState) {
    Object.entries(this.props).forEach(([key, val]) =>
      prevProps[key] !== val && console.log(`Prop '${key}' changed`)
    );
    Object.entries(this.state).forEach(([key, val]) =>
      prevState[key] !== val && console.log(`State '${key}' changed`)
    );

    // console.log(this.state.data)
  }

  showgen = (num) => {
    this.setState((state) =>
      state.sidebar = 'sidebar'
    )
    this.props.resetCurrentPage();
    this.setState({ showtype: false })
    this.setState({ showgens: true })
    this.setState({ searchItem: '' })
    let tempar = []
    if (num === 1) {
      tempar = []
      for (let i = 0; i < 151; i++) {
        tempar.push(this.state.data[i])
      }
      this.setState({ gentoshow: tempar })
    }
    else if (num === 2) {
      tempar = []
      for (let i = 151; i < 250; i++) {
        tempar.push(this.state.data[i])
      } this.setState({ gentoshow: tempar })
    }
    else if (num === 3) {
      tempar = []
      for (let i = 251; i < 385; i++) {
        tempar.push(this.state.data[i])
      } this.setState({ gentoshow: tempar })
    }
    else if (num === 4) {
      tempar = []
      for (let i = 386; i < 492; i++) {
        tempar.push(this.state.data[i])
      } this.setState({ gentoshow: tempar })
    }
    else if (num === 5) {
      tempar = []
      for (let i = 493; i < 648; i++) {
        tempar.push(this.state.data[i])
      } this.setState({ gentoshow: tempar })
    }
    else if (num === 6) {
      tempar = []
      for (let i = 649; i < 720; i++) {
        tempar.push(this.state.data[i])
      } this.setState({ gentoshow: tempar })
    }
    else if (num === 7) {
      tempar = []
      for (let i = 721; i < 808; i++) {
        tempar.push(this.state.data[i])
      } this.setState({ gentoshow: tempar })
    }
    else this.setState({ showgens: false })
  }

  searchInputhandler = (searchInput) => {
    this.props.resetCurrentPage();
    this.setState({ showtype: false })
    this.setState({ showgens: false })
    this.setState({ searchItem: searchInput })
  }

  showtype = (intype) => {
    this.setState((state) =>
      state.sidebar = 'sidebar'
    )
    this.props.resetCurrentPage();
    this.setState({ showgens: false })
    this.setState({ searchItem: '' })
    this.setState({ searchtype: intype })
    this.setState({ showtype: true })
  }

  sideHandler = () => {
    let side = this.state.sideStatus
    this.setState({ sideStatus: !side })
    if (this.state.sideStatus) {
      this.setState({ sidebar: 'onClickSide' })
    }
    else {
      this.setState({ sidebar: 'sidebar' })
    }
  }

  // shouldComponentUpdate(nextState,nextProps){
  //   return this.state !== nextState || this.props !== nextProps
  // }

  render() {
    console.log('pokecards.js rendering')

    let searchedItems = ''
    let searchedType = ''
    let pokemons = []
    let pokeDisplay = []

    if (this.state.showgens) {

      searchedItems = this.state.gentoshow.filter(
        (item) => {

          return item.Name.toLowerCase().indexOf(this.state.searchItem.toLowerCase()) !== -1
            || item.Abilities.toLowerCase().indexOf(this.state.searchItem.toLowerCase()) !== -1
            || item.ID.indexOf(this.state.searchItem) !== -1;
        }

      );
      pokemons = searchedItems.map((item) => {
        return <Pokecard
          imgSrc={item.ImgSrc}
          name={item.Name}
          id={item.ID}
          key={item.ID}
          abilities={item.Abilities}
        />
      })
    }

    else if (this.state.showtype) {

      searchedType = this.state.data.filter(
        (item) => {
          return item.Abilities.toLowerCase().indexOf(this.state.searchtype.toLowerCase()) !== -1;
        }
      );
      pokemons = searchedType.map((item) => {
        return <Pokecard
          imgSrc={item.ImgSrc}
          name={item.Name}
          id={item.ID}
          key={item.ID}
          abilities={item.Abilities}
        />
      })
    }
    else {
      searchedItems = this.state.data.filter(
        (item) => {
          return item.Name.toLowerCase().indexOf(this.state.searchItem.toLowerCase()) !== -1
            || item.Abilities.toLowerCase().indexOf(this.state.searchItem.toLowerCase()) !== -1
            || item.ID.indexOf(this.state.searchItem) !== -1
        }
      );
      pokemons = (searchedItems.map((item) => {
        return <Pokecard
          imgSrc={item.ImgSrc}
          name={item.Name}
          id={item.ID}
          key={item.ID}
          abilities={item.Abilities}
        />
      }))
    }

    for (let ind = this.props.start; ind < this.props.end; ind++) {
      pokeDisplay.push(pokemons[ind])
    }

    this.props.setNoOfPosts(this.state.showgens ? this.state.gentoshow.length : this.state.showtype ? searchedType.length : this.state.searchItem !== '' ? searchedItems.length : 809)

    return (
      <>
        <div className='toggle' onClick={this.sideHandler}>
          <div className='toggleLi'></div>
          <div className='toggleLi'></div>
          <div className='toggleLi'></div>
        </div>

        <h1 className='header'>Welcome to the National Pokedex</h1>

        <div className='searchbarDiv'>
          <input className="searchbar"
            value={this.state.searchItem}
            onChange={(event) => this.searchInputhandler(event.target.value)}
            type="text" placeholder="Search"
          />
        </div>

        <div className="pokecardsdiv">

        <Sidebar/>
          {/* <aside className={this.state.sidebar}>
            <h3 className="sideHeading">Pokemon Generations</h3>

            <div className="generations">
              <li className="gens" onClick={() => this.showgen(1)}>I</li>
              <li className="gens" onClick={() => this.showgen(2)}>II</li>
              <li className="gens" onClick={() => this.showgen(3)}>III</li>
              <li className="gens" onClick={() => this.showgen(4)}>IV</li>
              <li className="gens" onClick={() => this.showgen(5)}>V</li>
              <li className="gens" onClick={() => this.showgen(6)}>VI</li>
              <li className="gens" onClick={() => this.showgen(7)}>VII</li>
              <li className="gens" onClick={() => this.showgen(8)}>All</li>
            </div>

            <h3 className="sideHeading">Types</h3>

            <div className="types">
              <li className="pills pillGrass"
                onClick={() => this.showtype('grass')}>Grass</li>
              <li className="pills pillFire"
                onClick={() => this.showtype('fire')}>Fire</li>
              <li className="pills pillWater"
                onClick={() => this.showtype('water')}>Water</li>
              <li className="pills pillPoison"
                onClick={() => this.showtype('poison')}>Poison</li>
              <li className="pills pillFlying"
                onClick={() => this.showtype('flying')}>Flying</li>
              <li className="pills pillGround"
                onClick={() => this.showtype('ground')}>Ground</li>
              <li className="pills pillBug"
                onClick={() => this.showtype('bug')}>Bug</li>
              <li className="pills pillGhost"
                onClick={() => this.showtype('ghost')}>Ghost</li>
              <li className="pills pillNormal"
                onClick={() => this.showtype('normal')}>Normal</li>
              <li className="pills pillElectric"
                onClick={() => this.showtype('electric')}>Electric</li>
              <li className="pills pillFairy"
                onClick={() => this.showtype('fairy')}>Fairy</li>
              <li className="pills pillFighting"
                onClick={() => this.showtype('fighting')}>Fighting</li>
              <li className="pills Psychic"
                onClick={() => this.showtype('psychic')}>Psychic</li>
              <li className="pills pillGround"
                onClick={() => this.showtype('rock')}>Rock</li>
              <li className="pills pillSteel"
                onClick={() => this.showtype('steel')}>Steel</li>
              <li className="pills pillIce"
                onClick={() => this.showtype('ice')}>Ice</li>
              <li className="pills pillDragon"
                onClick={() => this.showtype('dragon')}>Dragon</li>
              <li className="pills pillDark"
                onClick={() => this.showtype('dark')}>Dark</li>
            </div>
          </aside> */}

          <div className="pokecards">
            {pokeDisplay}
          </div>



        </div>
      </>

    );
  }
}
