import React ,{useState}from 'react'


const Toggle = (props) => {

  const [toggle,setToggle] = useState(false);

  const clickHandler =() => {
    const status = toggle
    setToggle(!status)
    props.toggleHandler(toggle)
  }


    return(
        <div className='toggle'
          onClick={clickHandler}>
          <div className='toggleLi'></div>
          <div className='toggleLi'></div>
          <div className='toggleLi'></div>
        </div>
    );
}

export default Toggle
