import React from 'react';
import styles from './Pokecard.module.css';




const pokecard = (props) => {
    const cardbg = ['styles.card', 'styles.fire'];
    // if(props.abilities == 'fire'){
    //     cardbg.push('styles.fire')
    // }
    console.log(cardbg.join('  +  '))
    return(
        <div className={cardbg.join(' + ')}>
            <img src={props.imgSrc} alt="notfound"/>
            <h3>{props.name}</h3>
            <p>{props.id}</p>
            <p className="ability">{props.abilities}</p>               
        </div>
        );    
}

export default pokecard
