import React, { useState, useEffect} from 'react';
import './App.css';
import Pokecards from '../components/Pokecards/Pokecards';
import Footer from '../components/Footer/Footer'





const App = () => {

const [currentPage, setCurrentPage] = useState(1);
const [ postsPerPage, setPostsPerPage] = useState(100);

const indexOfLastPost = currentPage * postsPerPage;
const indexOfFirstPost = indexOfLastPost - postsPerPage;

const clickHandler = (num) => setCurrentPage(num);

console.log(indexOfFirstPost,indexOfLastPost)



  return (
    <div className="App">
      <h1 className='header'>Welcome to the National Pokedex</h1>
      <Pokecards  start={indexOfFirstPost} end={indexOfLastPost}/>

      <Footer postsPerPage={postsPerPage} totalPosts={809} clickHandler={clickHandler}/>



    </div>
  );

}

export default App;
