import React,{Component} from 'react';
import Pokecard from './Pokecard/Pokecard';
import './Pokecards.css';




export default class Pokecards extends Component {

  state = {
    data : [],
    gen : null,
  }

  componentWillMount(){
    const csvFilePath = require('../../assets/poke.csv');
    const Papa = require("papaparse/papaparse.min.js");
    Papa.parse(csvFilePath, {
      header: true,
      download: true,
      skipEmptyLines: true,
      complete: (result)=>this.updateData(result)
    })
  }

  updateData = (result) => {
    this.setState({data:result.data})
  }

  showgen = (number) => {
      console.log(number)
  }



  render(){


  return (
    <div >
      <input className="searchbar" type="text" placeholder="Search..."></input>
      <h3>Pokemon Generations</h3>
      <div className="generations">
        <li className="gens" onClick={()=>this.showgen(1)}><a > I </a></li>
        <li className="gens" onClick={()=>this.showgen(2)}><a > II </a></li>
        <li className="gens" onClick={()=>this.showgen(3)}><a > III </a></li>
        <li className="gens" onClick={()=>this.showgen(4)}><a > IV </a></li>
        <li className="gens" onClick={()=>this.showgen(5)}><a > V </a></li>
        <li className="gens" onClick={()=>this.showgen(6)}><a > VI </a></li>
        <li className="gens" onClick={()=>this.showgen(7)}><a > VII </a></li>
      </div>

      <div className="pokecards">
        {this.state.data.map((item)=>{
          return <Pokecard
              imgSrc = {item.ImgSrc}
              name = {item.Name}
              id = {item.ID}
              key = {item.ID}
              abilities =  {item.Abilities}  
              />          
        })}
      </div>

    </div>
  );
}
}
