import React,{Component} from 'react';
import Pokecard from './Pokecard/Pokecard';
import './Pokecards.css';




export default class Pokecards extends Component {

  state = {
    data : [],
    showgens : false,
    gentoshow:[],

  }

  componentWillMount(){
    const csvFilePath = require('../../assets/poke.csv');
    const Papa = require("papaparse/papaparse.min.js");
    Papa.parse(csvFilePath, {
      header: true,
      download: true,
      skipEmptyLines: true,
      complete: (result)=>this.updateData(result)
    })
  }

  updateData = (result) => {
    this.setState({data:result.data})
  }

  showgen = (num) => {
      if(num===1){
        for(let i=0;i<151;i++){
        this.state.gentoshow.push(this.state.data[i])
        }}
      else if(num===2){for(let i=151;i<250;i++){
        this.state.gentoshow.push(this.state.data[i])
        }}
      else if(num===3){for(let i=251;i<385;i++){
        this.state.gentoshow.push(this.state.data[i])
        }}
      else if(num===4){for(let i=386;i<492;i++){
        this.state.gentoshow.push(this.state.data[i])
        }}
      else if(num===5){for(let i=493;i<648;i++){
        this.state.gentoshow.push(this.state.data[i])
        }}
      else if(num===6){for(let i=649;i<720;i++){
        this.state.gentoshow.push(this.state.data[i])
        }}
      else if(num===7){for(let i=721;i<808;i++){
        this.state.gentoshow.push(this.state.data[i])
        }}
        console.log(this.state.gentoshow)

  }



  render(){
  let persons = []
  if(this.state.showgens==true){
    persons = (this.state.gentoshow.map((item)=>{
      return <Pokecard
          imgSrc = {item.ImgSrc}
          name = {item.Name}
          id = {item.ID}
          key = {item.ID}
          abilities =  {item.Abilities}  
          />}))
  }
  else {
    persons = (this.state.data.map((item)=>{
      return <Pokecard
          imgSrc = {item.ImgSrc}
          name = {item.Name}
          id = {item.ID}
          key = {item.ID}
          abilities =  {item.Abilities}  
          />}))
  }



  return (
    <div >
      <input className="searchbar" type="text" placeholder="Search Pokemon..."></input>
      <h3>Pokemon Generations</h3>
      <div className="generations">
        <li className="gens" onClick={()=>this.showgen(1)}><button>I</button></li>
        <li className="gens" onClick={()=>this.showgen(2)}><button>II</button></li>
        <li className="gens" onClick={()=>this.showgen(3)}><button>III</button></li>
        <li className="gens" onClick={()=>this.showgen(4)}><button>IV</button></li>
        <li className="gens" onClick={()=>this.showgen(5)}><button>V</button></li>
        <li className="gens" onClick={()=>this.showgen(6)}><button>VI</button></li>
        <li className="gens" onClick={()=>this.showgen(7)}><button>VII</button></li>
      </div>

      <div className="pokecards">



      </div>

    </div>
  );
}
}
