import React from 'react';
import styles from './Pokecard.module.css';




const pokecard = (props) => {
    let cardbg = '';
    if (props.abilities.includes("fire") === true) {
        cardbg.push('styles.fire')
    }
    console.log(cardbg);
    return (
        <div className={styles.card + ' ' + cardbg}>
            <div className={styles.imgBox}>
                <img src='' alt="notfound" />
            </div>
            <div className={styles.details}>
                <h3 className={styles.name}>
                    {props.name}<span className={styles.id}>
                    {props.id}
                    </span>
                </h3>
            <p ></p>
            <p className={styles.ability}>{props.abilities}</p></div>


        </div>
    );
}

export default pokecard
