import React,{Component} from 'react';
import './App.css';
import Pokecard from '../components/Pokecard/Pokecard';

export default class App extends Component {
  render(){
  const pokecards = []
  for(let i=1;i<600;i++){
    if(i<10){pokecards.push("00"+i)}
    else if(i<100){pokecards.push("0"+i)}
    else{
      pokecards.push(i)
    }    
  } 

  console.log(pokecards)


  return (
    <div className="App">
      {pokecards.map(i=> <Pokecard id={i}/>
      )}     
    </div>
  );}
}

