import React,{Component} from 'react';
import Pokecard from './Pokecard/Pokecard';
import './Pokecards.css';




export default class Pokecards extends Component {

  state = {
    data : [],
    gen : null,
    start:0,
    end:808,
  }

  componentWillMount(){
    const csvFilePath = require('../../assets/poke.csv');
    const Papa = require("papaparse/papaparse.min.js");
    Papa.parse(csvFilePath, {
      header: true,
      download: true,
      skipEmptyLines: true,
      complete: (result)=>this.updateData(result)
    })
  }

  updateData = (result) => {
    this.setState({data:result.data})
  }

  showgen = (num) => {

      if(num===1){
        this.setState({start:0}) 
        this.setState({end:150})
      }
      else if(num===2){
        this.setState({start:151}) 
        this.setState({end:250})
      }
      else if(num===3){
        this.setState({start:251}) 
        this.setState({end:385})
      }
      else if(num===4){
        this.setState({start:386}) 
        this.setState({end:492})
      }
      else if(num===5){
        this.setState({start:493}) 
        this.setState({end:648})
      }
      else if(num===6){
        this.setState({start:649}) 
        this.setState({end:720})
      }
      else if(num===7){
        this.setState({start:721})
        this.setState({end:808})
      }
  }



  render(){

  // for(let i =this.start;i=this.end;i++){
  //   <Pokecard
  //       imgSrc = {this.state.data[i].ImgSrc}
  //       name = {this.state.data[i].Name}
  //       id = {this.state.data[i].ID}
  //       key = {this.state.data[i].ID}
  //       abilities =  {this.state.data[i].Abilities}  
  //     />
  // }  
  let i = null



  return (
    <div >
      <input className="searchbar" type="text" placeholder="Search..."></input>
      <h3>Pokemon Generations</h3>
      <div className="generations">
        <li className="gens" onClick={()=>this.showgen(1)}><a > I </a></li>
        <li className="gens" onClick={()=>this.showgen(2)}><a > II </a></li>
        <li className="gens" onClick={()=>this.showgen(3)}><a > III </a></li>
        <li className="gens" onClick={()=>this.showgen(4)}><a > IV </a></li>
        <li className="gens" onClick={()=>this.showgen(5)}><a > V </a></li>
        <li className="gens" onClick={()=>this.showgen(6)}><a > VI </a></li>
        <li className="gens" onClick={()=>this.showgen(7)}><a > VII </a></li>
      </div>

      <div className="pokecards">
      for(i ={this.state.start};i={this.state.end};i++){
        <Pokecard
            imgSrc = {this.state.data[i].ImgSrc}
            name = {this.state.data[i].Name}
            id = {this.state.data[i].ID}
            key = {this.state.data[i].ID}
            abilities =  {this.state.data[i].Abilities}  
          />
      }        
})}
      </div>

    </div>
  );
}
}
