import React, {Component} from 'react'
import './Footer.css'


export default class Footer extends Component {

    state={
        first: 1,
    second: 2,
    third: 3,
    fourth: 4,
    fifth: 5,
    count:0
    }

    footerNext = () => {

        if(this.state.count <17){
            this.setState((state)=>{
                state.count = state.count+5

            })

            this.setState({
                first : first+count,
                second : second+count,
                third : third+count,
                fourth : fourth+count,
                fifth : fifth+count
              })
        }



      }

      footerPrev = () => {

        if(this.state.count!==0){
        this.setState((state)=>{
            state.count = state.count-5


        })
        this.setState((state)=>{
            state.first = state.first-state.count
            state.second = state.second-state.count
            state.third = state.third-state.count
            state.fourth = state.fourth-state.count
            state.fifth = state.fifth-state.count
          })
        }
        }



    render(){
        console.log('footer rendering')
        console.log(this.state.count)
        return(

            <footer className='footer'>
            <li className='footerli' onClick={() => this.footerPrev()}>Previous</li>
            <li className='footerli' onClick={() => this.clickHandler(this.state.first)}>{this.state.first}</li>
            <li className='footerli' onClick={() => this.clickHandler(this.state.second)}>{this.state.second}</li>
            <li className='footerli' onClick={() => this.clickHandler(this.state.third)}>{this.state.third}</li>
            <li className='footerli' onClick={() => this.clickHandler(this.state.fourth)}>{this.state.fourth}</li>
            <li className='footerli' onClick={() => this.clickHandler(this.state.fifth)}>{this.state.fifth}</li>
            <li className='footerli' onClick={() => this.footerNext()}>Next</li>




          </footer>
        );
    }
}
