import React, { Component } from 'react';
import './App.css';
import Pokecards from '../components/Pokecards/Pokecards';




class App extends Component {

  state = {
    page: 1,
    start: 0,
    end: 49,

  }



  clickHandler = (num) => {

    this.setState({ page: num })
    if (this.state.page === 1) {
      this.setState({
        start: 0,
        end: 39
      })
    }
    else if (this.state.page === 2) {
      this.setState({
        start: 50,
        end: 99
      })
    }
  }

    footerNext = () => {
      let nextCount=this.state.nextCount
      this.setState({nextCount:nextCount+1})

      this.setState({
        first: 6,
        second: 7,
        third: 8,
        fourth: 9,
        fifth: 10
      })

    }

    footerPrev = () => {
      let nextCount=this.state.nextCount

      this.setState({nextCount:nextCount-1})
      if(this.state.nextCount<0){
        this.setState({nextCount:0})
      }
      this.setState({
        first: 1,
        second: 2,
        third: 3,
        fourth: 4,
        fifth: 5
      })

    }


    render() {
      console.log('app.js rendering')
      console.log(this.state.nextCount)






      return (
        <div className="App">
          <h1 className='header'>Welcome to the National Pokedex</h1>
          <Pokecards start={this.state.start}
            end={this.state.end} />




        </div>
      );
    }
  }

  export default App
