import React ,{useState}from 'react'
import Toggle from '../SideDrawer/Toggle'

const Header = (props) => {

    const [status,setStatus] = useState();

    const toggleHandler = (status) => {
        setStatus(status)
        console.log(status)

    }
    return (
        <div className=''>
            <Toggle toggleHandler={toggleHandler}/> 
            <h1 className='header'>Welcome to the National Pokedex</h1>

            <div className='searchbarDiv'>
                <input className="searchbar"
                    // value={this.state.searchItem}
                    onChange={(event) => this.searchInputhandler(event.target.value)}
                    type="text" placeholder="Search"
                />
            </div>
        </div>
    );
}

export default Header
